<script lang="ts" setup>
import {dateRuRU, lightTheme, ruRU} from "naive-ui";
import themeOverrides from "@/assets/style/naive-ui-theme-overrides.json"
import {useRouter} from "vue-router";
import {computed, provide, Ref, ref, watchEffect} from "vue";
import axios from "axios";

const router = useRouter()

const baseApiUrl: Ref<string> = ref("http://localhost:8000/api")
const baseApiUrlError = ref(false)
const showSettingsModal = ref(false)

const axiosInstance = computed(() => axios.create({baseURL: baseApiUrl.value}))

watchEffect(async () => {
  try {
    baseApiUrlError.value = (await axiosInstance.value.get("/check")).data !== "Success"
  } catch (e) {
    baseApiUrlError.value = true
  }
})

provide("axiosInstance", axiosInstance)
</script>

